import React,{Fragment,useState} from 'react';
import {Text, View,Modal} from 'react-native';
import styles from './styles';

const Mostrar= ({nome,descrição,visibleM}) => {

    const [visible,setvisible] = useState(visibleM);
    console.log("mds"+visibleM)
    console.log("mds"+visible)
    return(
    <View>
        <Modal
        animationType={'slide'}
        transparent={false}
        visible= {visible}
        onRequestClose={() => {setvisible(false);}}
        >
        <Text>Informação Nota:</Text>
        <Text>{nome}</Text>
        <Text>{descrição}</Text>
        </Modal>

    </View>

    );
}
export default Mostrar;
