import {StyleSheet,Dimensions} from 'react-native';
const largura= Dimensions.get("screen").width;

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#87ceebff',
      alignItems: 'center',
      justifyContent: 'center',
      borderRadius: 30,
      margin:10,
    },
    mais: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
      width:largura/5, 
      height:largura/5,
      margin:100,
      flexDirection: 'row',
    },
  });
export default styles;
