import React,{Fragment,useState} from 'react';
import {Text, TouchableOpacity, View} from 'react-native';
import Mostrar from '../MostrarNotas';
import styl from './styles';

const Notas= ({nota}) => {
    const [visiblem,setvisiblem] = useState(false);
    const modalNota= ()=>{
        setvisiblem(true);
        console.log("apertou")
        this.deleteItemById(nota.id);
      }    
    return(
        <View>
            <TouchableOpacity onPress={modalNota}>
                <View style={styl.container}>
                    <Text>{nota.nome}</Text>
                </View>
            </TouchableOpacity>
                <View>
                    <Mostrar nome={nota.nome} descrição={nota.texto} visibleM={visiblem}/>
                </View>
        </View>      
    );
}
export default Notas;
